import React from 'react';
//import { Button } from 'bootstrap'; // Import Bootstrap Button component
//import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import axios from 'axios';


export default function NoteScreen() {

    async function addNote() {
        const title = document.getElementById("Notes_title").value;
        const content = document.getElementById("Notes_content").value;

        const newNote = {
            title: title,
            content: content,
          };

          try {
            await axios.post(process.env.APP_BACKEND_LINK + "/notes", newNote);
            //addBookMessage.innerHTML = `<span style='color:green;'>Book added successfully!</span>`;
          } catch {
            //addBookMessage.innerHTML = `<span style='color:red;'>Something went wrong. Try Again.</span>`;
          }
    }

    return (
      <div>
        <h1>Notes Screen</h1>
        {/* Add your note content here */}

        {/* Bootstrap Button */}
        <div class="notes__sidebar">
            <button class="notes__add btn-primary rounded-3" onClick={addNote} id="addNotesButton">Add Note</button>
            <div class="notes__list" id="theNotes_list">

            </div>
        </div>

        <div class="notes__preview">
            <input class="notes__title form-control" id="Notes_title" type="text" placeholder="Enter a title..."/>
            <span>
                <button class="saveNoteTitle btn-primary rounded-3" id="saveTitleButton" onclick="addNote()">Save Title</button>
                <button class="saveNote btn-primary rounded-3">Save Note</button>
            </span>


            <textarea class="notes__body form-control" id="Notes_content" placeholder="I am the notes body..."></textarea> 
        </div>
      </div>
    );

}
